-
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  12,  28, 'Therefore tell them, Thus says the Lord<H0136> ForeverOne<H3068>: None of my words shall be deferred any more, but the word which I shall speak shall be performed, says the Lord<H0136> ForeverOne<H3068>.');
