-
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  13,   3, 'Thus says the Lord<H0136> ForeverOne<H3068>, Woe to the foolish prophets, who follow their own spirit, and have seen nothing!');
