INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  13,   8, 'Therefore thus says the Lord<H0136> ForeverOne<H3068>: Because you have spoken falsehood, and seen lies, therefore, behold, I am against you, says the Lord<H0136> ForeverOne<H3068>.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  13,   9, 'My hand shall be against the prophets who see false visions, and who divine lies: they shall not be in the council of my people, neither shall they be written in the writing of the house of Israel, neither shall they enter into the land of Israel; and you shall know that I am the Lord<H0136> ForeverOne<H3068>.');
