INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  16,   8, 'Now when I passed by you, and looked at you, behold, your time was the time of love; and I spread my skirt over you, and covered your nakedness: yes, I swore to you, and entered into a covenant with you, says the Lord<H0136> ForeverOne<H3068>, and you became mine.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  16,   9, 'Then washed I you with water; yes, I thoroughly washed away your blood from you, and I anointed you with oil.');
-
-
-
-
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  16,  14, 'Your renown went forth among the nations for your beauty; for it was perfect, through my majesty which I had put on you, says the Lord<H0136> ForeverOne<H3068>.');
-
