INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  16,  23, 'It has happened after all your wickedness, (woe, woe to you! says the Lord<H0136> ForeverOne<H3068>),');
