INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  35,  14, 'Thus says the Lord<H0136> ForeverOne<H3068>: When the whole earth rejoices, I will make you desolate.');
