INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EZE',  36,  33, 'Thus says the Lord<H0136> ForeverOne<H3068>: In the day that I cleanse you from all your iniquities, I will cause the cities to be inhabited, and the waste places shall be built.');
