INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   3,  12, 'There are certain Jews whom you have appointed over the affairs of the province of Babylon: Shadrach, Meshach, and Abednego; these men, O king, have not respected you. They don\'t serve your god<H0426>, nor worship the golden image which you have set up.');
-
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   3,  14, 'Nebuchadnezzar answered them, Is it on purpose, Shadrach, Meshach, and Abednego, that you don\'t serve my god<H0426>, nor worship the golden image which I have set up?');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   3,  15, 'Now if you are ready whenever you hear the sound of the horn, flute, zither, lyre, harp, pipe, and all kinds of music to fall down and worship the image which I have made, [well]: but if you don\'t worship, you shall be cast the same hour into the midst of a burning fiery furnace; and who is that god<H0426> that shall deliver you out of my hands?');
-
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   3,  17, 'If it be [so], our God<H0426> whom we serve is able to deliver us from the burning fiery furnace; and he will deliver us out of your hand, O king.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   3,  18, 'But if not, be it known to you, O king, that we will not serve your god<H0426>, nor worship the golden image which you have set up.');
