INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   6,  22, 'My God<H0426> has sent his angel, and has shut the lions\' mouths, and they have not hurt me; because as before him innocence was found in me; and also before you, O king, have I done no hurt.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('DAN',   6,  23, 'Then was the king exceeding glad, and commanded that they should take Daniel up out of the den. So Daniel was taken up out of the den, and no kind of harm was found on him, because he had trusted in his God<H0426>.');
