INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('MAT',   5,  21, '\"You have heard that it was said to the ancient ones, \'You shall not murder;\' and \'Whoever murders will be in danger of the judgment.\'');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('MAT',   5,  22, 'But I tell you that everyone who is angry with his brother without a cause will be in danger of the judgment. Whoever says to his brother, \'Raca!\' will be in danger of the council. Whoever says, \'You fool!\' will be in danger of the fire of Gehenna.');
