INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('MAT',  13,  12, 'For whoever has, to him will be given, and he will have abundance; but whoever doesn\'t have, from him will be taken away even that which he has.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('MAT',  13,  13, 'So I speak to them in parables, because seeing they don\'t see, and hearing, they don\'t hear. Nor do they understand.');
