INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 19, 1, 'The two angels came to Sodom at evening. Lot sat in the gate of Sodom. Lot saw them, and rose up to meet them. He bowed himself with his face to the earth,');
