INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 19, 9, 'They said, \"Stand back!\" Then they said, \"This one fellow came in to live as a foreigner, and he appoints himself a judge. Now will we deal worse with you, than with them!\" They pressed hard on the man Lot, and drew near to break the door.');
--('GEN',  18,  16, 'The men rose up from there, and looked toward Sodom. Abraham went with them to see them on their way.'),
