INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 24, 46, 'She hurried and let down her pitcher from her shoulder, and said, \'Drink, and I will also give your camels a drink.\' So I drank, and she also gave the camels a drink.');
