--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('GEN',  24,  31, 'He said, \"Come in, you blessed of TheIAM<H3068>. Why do you stand outside? For I have prepared the house, and room for the camels.\"'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 24, 58, 'They called Rebekah, and said to her, \"Will you go with this man?\" She said, \"I will go.\"');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('GEN',  24,  32, 'The man came into the house, and he unloaded the camels. He gave straw and feed for the camels, and water to wash his feet and the feet of the men who were with him.'),
