--('GEN',  24,  34, 'He said, \"I am Abraham\'s servant.'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 24, 61, 'Rebekah arose with her ladies. They rode on the camels, and followed the man. The servant took Rebekah, and went his way.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('GEN',  24,  35, 'TheIAM<H3068> has blessed my master greatly. He has become great. TheIAM<H3068> has given him flocks and herds, silver and gold, male servants and female servants, and camels and donkeys.'),
