INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 24, 65, 'She said to the servant, \"Who is the man who is walking in the field to meet us?\" The servant said, \"It is my master<H0113>.\" She took her veil, and covered herself.');
