INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 24, 67, 'Isaac brought her into his mother Sarah\'s tent, and took Rebekah, and she became his wife. He loved her. Isaac was comforted after his mother\'s death.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('GEN',  24,  41, 'Then you will be clear from my oath, when you come to my relatives. If they don\'t give her to you, you shall be clear from my oath.\''),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 25, 1, 'Abraham took another wife, and her name was Keturah.');
