INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 25, 9, 'Isaac and Ishmael, his sons, buried him in the cave of Machpelah, in the field of Ephron, the son of Zohar the Hittite, which is before Mamre,');
