INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 31, 35, 'She said to her father, \"Don\'t let my lord<H0113> be angry that I can\'t rise up before you; for I\'m having my period.\" He searched, but didn\'t find the teraphim.');
