INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('GEN', 37, 22, 'Reuben said to them, \"Shed no blood. Throw him into this pit that is in the wilderness, but lay no hand on him\" - that he might deliver him out of their hand, to restore him to his father.');
--('GEN',  36,  37, 'Samlah died, and Shaul of Rehoboth by the river, reigned in his place.'),
