INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 18, 10, 'Jethro said, \"Blessed be TheIAM<H3068>, who has delivered you out of the hand of the Egyptians, and out of the hand of Pharaoh; who has delivered the people from under the hand of the Egyptians.');
