--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  16,  32, 'Moses said, \"This is the thing which TheIAM<H3068> has commanded, \'Let an omer-full of it be kept throughout your generations, that they may see the bread with which I fed you in the wilderness, when I brought you out of the land of Egypt.\'\"'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 18, 12, 'Jethro, Moses\' father-in-law, took a burnt offering and sacrifices for God<H0430>. Aaron came with all of the elders of Israel, to eat bread with Moses\' father-in-law before God<H0430>.');
