INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 18, 21, 'Moreover you shall provide out of all the people able men, such as fear God<H0430>: men of truth, hating unjust gain; and place such over them, to be rulers of thousands, rulers of hundreds, rulers of fifties, and rulers of tens.');
