INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 24, 'You shall overlay it with pure gold, and make a gold molding around it.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  24,  11, 'He didn\'t lay his hand on the nobles of the children of Israel. They saw God<H0430>, and ate and drank.'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 25, 'You shall make a rim of a handbreadth around it. You shall make a golden molding on its rim around it.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  24,  12, 'TheIAM<H3068> said to Moses, \"Come up to me on the mountain, and stay here, and I will give you the stone tablets with the law and the commands that I have written, that you may teach them.\"'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 26, 'You shall make four rings of gold for it, and put the rings in the four corners that are on its four feet.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  24,  13, 'Moses rose up with Joshua, his servant, and Moses went up onto God<H0430>\'s Mountain.'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 27, 'the rings shall be close to the rim, for places for the poles to carry the table.');
--('EXO',  24,  14, 'He said to the elders, \"Wait here for us, until we come again to you. Behold, Aaron and Hur are with you. Whoever<H1167> is involved in a dispute can go to them.\"'),
