INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 31, '\"You shall make a lampstand of pure gold. Of hammered work shall the lampstand be made, even its base, its shaft, its cups, its buds, and its flowers, shall be of one piece with it.');
--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  24,  18, 'Moses entered into the middle of the cloud, and went up on the mountain; and Moses was on the mountain forty days and forty nights.'),
