--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  25,   1, 'TheIAM<H3068> spoke to Moses, saying,'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 25, 33, 'three cups made like almond blossoms in one branch, a bud and a flower; and three cups made like almond blossoms in the other branch, a bud and a flower, so for the six branches going out of the lampstand;');
--('EXO',  25,   2, '\"Speak to the children of Israel, that they take an offering for me. From everyone whose heart makes him willing you shall take my offering.'),
