INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 26, 6, 'You shall make fifty clasps of gold, and couple the curtains one to another with the clasps: and the tabernacle shall be a unit.');
--('EXO',  25,  15, 'The poles shall be in the rings of the ark. They shall not be taken from it.'),
INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 26, 7, '\"You shall make curtains of goats\' hair for a covering over the tabernacle. You shall make them eleven curtains.');
