--INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('EXO',  25,  17, 'You shall make a mercy seat of pure gold. Two and a half cubits shall be its length, and a cubit and a half its width.'),
