INSERT INTO verses (bookCode, chapter, verseNumber, verseText) VALUES('EXO', 26, 10, 'You shall make fifty loops on the edge of the one curtain that is outmost in the coupling, and fifty loops on the edge of the curtain which is outmost in the second coupling.');
