INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,   5, 'When Elihu saw that there was no answer in the mouth of these three men, his wrath was kindled.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,   6, 'Elihu the son of Barachel the Buzite answered, I am young, and you are very old. Therefore I held back, and didnt dare show you my opinion.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,   7, 'I said, Days should speak, and multitude of years should teach wisdom.');
