INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,   9, 'It is not the great who are wise, nor the aged who understand justice.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  10, 'Therefore I said, Listen to me; I also will show my opinion.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  11, 'Behold, I waited for your words, and I listened for your reasoning, while you searched out what to say.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  12, 'Yes, I gave you my full attention, but there was no one who convinced Job, or who answered his words, among you.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  13, 'Beware lest you say, We have found wisdom. God may refute him, not man;');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  14, 'for he has not directed his words against me; neither will I answer him with your speeches.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  15, 'They are amazed. They answer no more. They dont have a word to say.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  16, 'Shall I wait, because they dont speak, because they stand still, and answer no more?');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  17, 'I also will answer my part, and I also will show my opinion.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  18, 'For I am full of words. The spirit within me constrains me.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  19, 'Behold, my breast is as wine which has no vent; like new wineskins it is ready to burst.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('JOB',  31,  20, 'I will speak, that I may be refreshed. I will open my lips and answer.');
