INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',   1,  30, 'For ye shall be as an oak whose leaf fadeth, and as a garden that hath no water.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',   2,   3, 'And many people shall go and say, Come ye, and let us go up to the mountain of the \nd LORD\nd*, to the house of the God{H0430} of Jacob; and he will teach us of his ways, and we will walk in his paths: for out of Zion shall go forth the law, and the word of the \nd LORD\nd* from Jerusalem.');
