INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',   3,  10, 'Say ye to the righteous, that \add it shall be\add* well \add with him:\add* for they shall eat the fruit of their doings.');
