INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  31,   7, 'For in that day every man shall cast away his idols of silver, and his idols of gold, which your own hands have made unto you \add for\add* a sin.');
