INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  32,  10, 'Many days and years shall ye be troubled, ye careless women: for the vintage shall fail, the gathering shall not come.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  32,  11, 'Tremble, ye women that are at ease; be troubled, ye careless ones: strip you, and make you bare, and gird \add sackcloth\add* upon \add your\add* loins.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  32,  20, 'Blessed \add are\add* ye that sow beside all waters, that send forth \add thither\add* the feet of the ox and the ass.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  33,   4, 'And your spoil shall be gathered \add like\add* the gathering of the caterpiller: as the running to and fro of locusts shall he run upon them.');
