INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  33,  13, 'Hear, ye \add that are\add* far off, what I have done; and, ye \add that are\add* near, acknowledge my might.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  34,   1, 'Come near, ye nations, to hear; and hearken, ye people: let the earth hear, and all that is therein; the world, and all things that come forth of it.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  34,  16, 'Seek ye out of the book of the \nd LORD\nd*, and read: no one of these shall fail, none shall want her mate: for my mouth it hath commanded, and his spirit it hath gathered them.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  35,   3, 'Strengthen ye the weak hands, and confirm the feeble knees.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  35,   4, 'Say to them \add that are\add* of a fearful heart, Be strong, fear not: behold, your God{H0430} will come \add with\add* vengeance, \add even\add* God{H0430} \add with\add* a recompence; he will come and save you.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('ISA',  36,   4, 'And Rabshakeh said unto them, Say ye now to Hezekiah, Thus saith the great king, the king of Assyria, What confidence \add is\add* this wherein thou trustest?');
