INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('MAT',  13,  11, 'He answered and said unto them, Because it is given unto you to know the mysteries of the kingdom of heaven, but to them it is not given.');
