INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  11,  41, 'But rather give alms of such things as ye have; and, behold, all things are clean unto you.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  11,  42, 'But woe unto you, Pharisees! for ye tithe mint and rue and all manner of herbs, and pass over judgment and the love{G0025} of God: these ought ye to have done, and not to leave the other undone.');
