INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  12,   5, 'But I will forewarn you whom ye shall fear: Fear him, which after he hath killed hath power to cast into hell; yea, I say unto you, Fear him.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  12,   7, 'But even the very hairs of your head are all numbered. Fear not therefore: ye are of more value than many sparrows.');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  12,   8, 'Also I say unto you, Whosoever shall confess me before men, him shall the Son of man also confess before the angels of God:');
INSERT INTO `verses` (`bookCode`, `chapter`, `verseNumber`, `verseText`) VALUES ('LUK',  12,  11, 'And when they bring you unto the synagogues, and \+add unto\+add* magistrates, and powers, take ye no thought how or what thing ye shall answer, or what ye shall say:');
